mapper.select:
select unt.unity_code, unt.unity_name, unt.unity_tag, unt.unity_thumbnail_path, unt.unity_intro, 'joined' as unity_type from _ringo_unity unt join _ringo_unity_member untm ON unt.unity_code = untm.unity_code where untm.user_code = #{user_code}
union all
select unt.unity_code, unt.unity_name, unt.unity_tag, unt.unity_thumbnail_path, NULL as unity_last_post, 'favorite' as unity_type from _ringo_unity unt join _ringo_favorite f on unt.unity_code = f.target_code where f.user_code = #{user_code}

select *
from unity
where unity_code = #{unity_code} 
order by ub_category_order asc, ub_board_order asc;

mapper.update:
update _ringo_unity_board
set ub_category_name = #{ub_category_name},
	ub_category_order = #{ub_category_order}
where ub_category_code = #{ub_category_code}

update _ringo_unity_board
set ub_board_name = #{ub_board_name},
	ub_board_order = #{ub_board_order}
where ub_board_code = #{ub_board_code}
