mapper.select:
select user_code from _ringo_favorite where target_code = #{user_code}
union all
select user_code from _ringo_follow where target_code = #{user_code}

select u.user_nickname, u.user_nation, u.user_birth, u.user_native_lang, u.user_fluent_lang, u.user_learning_lang, u.user_intro, u.user_topic, u.user_objective, u.user_ideal_partner, u.user_thumbnail_path, u.user_profile_path, a.user_tags from _ringo_user u left join _ringo_algorithm a on a.user_code = u.user_code where not exists ( select 1 from _ringo_follow fw where fw.target_code = u.user_code and fw.user_code = #{user_code} ) and not exists ( select 1 from _ringo_favorite fv where fv.target_code = u.user_code and fv.user_code = #{user_code} ) and
a.user_tags like CONCAT('%', #{tag}, '%')
and u.user_code = #{user_code}
